import { ReactNode } from "react";
import { Card, CardContent } from "./ui/card";

interface TestimonialCardProps {
  quote: string;
  author: string;
  role: string;
  image?: string;
}

const TestimonialCard = ({ quote, author, role, image }: TestimonialCardProps) => {
  return (
    <Card className="border border-slate-200 hover:border-slate-300 hover:shadow-md transition-all duration-300 overflow-hidden">
      <CardContent className="p-6">
        <div className="flex items-start">
          {image && (
            <div className="flex-shrink-0 mr-4">
              <img
                className="h-12 w-12 rounded-full object-cover"
                src={image}
                alt={author}
              />
            </div>
          )}
          <div>
            <p className="text-slate-600 italic mb-4 text-base">"{quote}"</p>
            <div className="font-medium text-slate-900">{author}</div>
            <div className="text-slate-500 text-sm">{role}</div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default TestimonialCard;
